import React, { useEffect, useState } from 'react';

function App() {
  const [livros, setLivros] = useState([]);
  const [titulo, setTitulo] = useState('');
  const [autor, setAutor] = useState('');

  // Buscar os livros na API
  useEffect(() => {
    fetch('http://localhost:5176/api/livros')
      .then(res => res.json())
      .then(data => setLivros(data))
      .catch(err => console.error("Erro ao buscar livros:", err));
  }, []);

  // Enviar novo livro
  const adicionarLivro = () => {
    fetch('http://localhost:5176/api/livros', {
      method: 'POST',
      headers: { 'Content-Type': 'application/json' },
      body: JSON.stringify({ titulo, autor })
    })
    .then(res => res.json())
    .then(novoLivro => {
      setLivros([...livros, novoLivro]);
      setTitulo('');
      setAutor('');
    })
    .catch(err => console.error("Erro ao adicionar livro:", err));
  };

  // Deletar livro
  const removerLivro = (id) => {
    fetch(`http://localhost:5176/api/livros/${id}`, {
      method: 'DELETE'
    })
    .then(() => setLivros(livros.filter(livro => livro.id !== id)))
    .catch(err => console.error("Erro ao remover livro:", err));
  };

  return (
    <div style={{ padding: '20px' }}>
      <h1>Sistema de Livros</h1>

      <div>
        <input
          type="text"
          placeholder="TÃ­tulo"
          value={titulo}
          onChange={e => setTitulo(e.target.value)}
        />
        <input
          type="text"
          placeholder="Autor"
          value={autor}
          onChange={e => setAutor(e.target.value)}
        />
        <button onClick={adicionarLivro}>Adicionar Livro</button>
      </div>

      <h2>Lista de Livros</h2>
      <ul>
        {livros.map(livro => (
          <li key={livro.id}>
            {livro.titulo} - {livro.autor}
            <button onClick={() => removerLivro(livro.id)}>Remover</button>
          </li>
        ))}
      </ul>
    </div>
  );
}

export default App;
